                                           F@_2,
                                           F@_3,
                                           F@_4,
                                           TrUserData);
        32 ->
            d_field_route_summary_elapsed_time(Rest,
                                               0,
                                               0,
                                               F@_1,
                                               F@_2,
                                               F@_3,
                                               F@_4,
                                               TrUserData);
        _ ->
            case Key band 7 of
                0 ->
                    skip_varint_route_summary(Rest,
                                              0,
                                              0,
                                              F@_1,
                                              F@_2,
                                              F@_3,
                                              F@_4,
                                              TrUserData);
                1 ->
                    skip_64_route_summary(Rest,
                                          0,
